	{
		function TestCase_DeployerTargetAPI_RunInstallScripts_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}
		
		var strUrl = '';
		strUrl += x_snc_devtools.GetInstanceURL();
		strUrl += '/api/x_snc_deployer/target/run_install_scripts';
