		}
		if (bStopOnError !== null)
		{
			strUrl += 'stop_on_error='+x_snc_devtools.GetBoolValue(bStopOnError);
		}	
		
		test.TestCase_HttpRequest(
			'Deployer Target API / Run Install Scripts',
			{
				url: strUrl,
				basicauth: oBasicAuth
			},
			vExpected,
			TestCase_DeployerTargetAPI_RunInstallScripts_CheckResult);

	}

