sys_script_include.script:
function GetStatus()
{
	var IsFunction = x_snc_devtools.IsFunction;
	var GetStringValue = x_snc_devtools.GetStringValue;

	var status = {};

	status.apps = GetApplicationStatus();

	var grAppDeployer = GetAppRecord('x_snc_deployer');	
	status.deployer_version = GetStringValue(grAppDeployer.version);

	if (IsFunction(x_snc_codesanity.GetScanSuiteRecord) == true)
	{
		var grScanSuite = x_snc_codesanity.GetScanSuiteRecord();
		status.codesanity_scansuite_sys_id = GetStringValue(grScanSuite.sys_id);
	}	

	Debug(status);

	return status;
}
