sys_script_include.script:
function TargetInstanceGetApplicationList(grInstance)
{
	var GetValue = x_snc_devtools.GetValue;
	var IsArray = x_snc_devtools.IsArray;

	Debug('TargetInstanceGetApplicationList');

	var result = TargetInstanceRequest(grInstance,'x_snc_deployer/target/get_status');

	Debug('after TargetInstanceRequest');
	Debug(result);

	var aApps = GetValue(result,'result','apps');

	Debug(aApps);

	if (IsArray(aApps) != true)
	{
		return false;
	}

	return aApps;
}
