sys_script_include.script:
function GetApplicationStatus()
{
	var Clone = x_snc_devtools.Clone;

	var aApps = [];

	var grApps = new GlideRecord('sys_app');
	grApps.orderBy('name');
	grApps.query();
	while (grApps.next())
	{
		var app = Clone(grApps);
		delete app.can_edit_in_studio;
		delete app.enforce_license;
		delete app.guided_setup_guid;
		delete app.hide_on_ui;
		delete app.installed_as_dependency
		delete app.licensable;
		delete app.license;
		delete app.license_category;
		delete app.license_definition;
		delete app.license_model;
		delete app.logo;
		delete app.menu;
		delete app.private;
		delete app.restrict_table_access;
		delete app.runtime_access_tracking;
		delete app.source;
		delete app.sys_created_by;
		delete app.sys_created_on;
		delete app.sys_meta;
		delete app.sys_mod_count;
		delete app.sys_tags;
		delete app.sys_updated_by;
		delete app.sys_updated_on;
		delete app.template;
		delete app.trackable;
		delete app.uninstall_blocked;
		delete app.user_role;

		aApps.push(app);
	}

	var grCustomizations = new GlideRecord('sys_app_customization');
	grCustomizations.query();
	while (grCustomizations.next())
	{
		var custom = Clone(grCustomizations);
		aApps.push(custom);
	}

	return aApps;
}
