sys_script_include.script:
function ExecuteNextDeploymentTaskForDeployment(grDeployment)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	if (IsValidRecord(grDeployment) == false)
	{
		return false;
	}

	if (grDeployment.state != GetDeploymentState('inprogress'))
	{
		return false;
	}

	var grTask = new GlideRecord('x_snc_deployer_deployment_task');
	grTask.addQuery('deployment','=',grDeployment.sys_id);
	grTask.addQuery('state','=',GetDeploymentTaskState('ready'));
	grTask.setLimit(1);
	grTask.query();
	if (grTask.next() == false)
	{
		var grTask = new GlideRecord('x_snc_deployer_deployment_task');

		grTask.addQuery('deployment','=',grDeployment.sys_id);
		grTask.orderBy('order');
		grTask.addQuery('state','=',GetDeploymentTaskState('pending'));
		grTask.setLimit(1);
		grTask.query();
		if (grTask.next() == false)
		{
			return false;
		}
	}

	if (grTask.execution == 'manual')
	{
		grTask.state = GetDeploymentTaskState('inprogress');
		grTask.update();

	}
	else if (grTask.execution == 'automatic')
	{
		// Do the lengthy operation here
	}


	return true;
}
