sys_script_include.script:
function GetDeploymentTasks(status,instance)
{
	var IsObject = x_snc_devtools.IsObject;
	var GetValue = x_snc_devtools.GetValue;
	var GetArrayValue = x_snc_devtools.GetArrayValue;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetBoolValue = x_snc_devtools.GetBoolValue;


	if (IsObject(status) == false)
	{
		return false;
	}
	if (IsObject(instance) == false)
	{
		return false;
	}

	var strInstanceName = GetStringValue(GetValue(instance,'name'));
	if (strInstanceName == '')
	{
		return false;
	}

	var bCodeSanity = GetBoolValue(GetValue(instance,'codesanity'));
	var bDefaultATF = GetBoolValue(GetValue(instance,'atf_app_default'));


	var aAppsOnTarget = GetApplicationStatusFromInstance(instance);
	Debug(aAppsOnTarget);

	var aApps = GetArrayValue(GetValue(status,'list'));

	var aTasks = [];


	var app = {};

	// Install all apps
	for (var nApp = 0; nApp < aApps.length; nApp++)
	{
		app = aApps[nApp];

		var strAppName = app.name + ' ' + app.version;

		var task = {};
		task.name = 'Install application "'+strAppName+'" on instance "'+strInstanceName+'"';
		aTasks.push(task);

		if (bCodeSanity == true)
		{
			var task = {};
			task.name = 'Execute CodeSanity checks on application "'+strAppName+'" on instance "'+strInstanceName+'"';
			aTasks.push(task);		
		}

	}


	// Run installation scripts
	task = {};
	task.name = 'Run Installation scripts on instance "'+strInstanceName+'" (via API on application "'+app.name+'")';
	aTasks.push(task);

	// Run application default ATF Test Suites
	if (bDefaultATF == true)
	{
		for (var nApp = 0; nApp < aApps.length; nApp++)
		{
			var app = aApps[nApp];

			var strAppName = app.name + ' ' + app.version;

			var task = {};
			task.name = 'Execute application default ATF Test Suite "'+strAppName+'" on instance "'+strInstanceName+'"';
			aTasks.push(task);
		}
	}

	status.tasks = aTasks;

	return status;
}
