sys_script_include.script:
function GetDeploymentTasks(status,instance)
{
	var IsObject = x_snc_devtools.IsObject;
	var GetValue = x_snc_devtools.GetValue;
	var GetArrayValue = x_snc_devtools.GetArrayValue;
	var GetStringValue = x_snc_devtools.GetStringValue;


	if (IsObject(status) == false)
	{
		return false;
	}
	if (IsObject(instance) == false)
	{
		return false;
	}

	var strInstanceName = GetStringValue(GetValue(instance,'name'));
	if (strInstanceName == '')
	{
		return false;
	}

	var aApps = GetArrayValue(GetValue(status,'list'));

	var aTasks = [];



	// Install all apps
	for (var nApp = 0; nApp < aApps.length; nApp++)
	{
		var app = aApps[nApp];

		var strAppName = app.name + ' ' + app.version;

		var task = {};
		task.name = 'Install app '+strAppName+' on instance '+strInstanceName;
		aTasks.push(task);

		var task = {};
		task.name = 'Execute CodeSanity checks on '+strAppName+' on instance '+strInstanceName;
		aTasks.push(task);		
	}


	// Run installation scripts
	task = {};
	task.name = 'Run Installation scripts on instance '+strInstanceName;
	aTasks.push(task);


	// Run ATF tests
	for (var nApp = 0; nApp < aApps.length; nApp++)
	{
		var app = aApps[nApp];

		var strAppName = app.name + ' ' + app.version;

		var task = {};
		task.name = 'Run ATF tests for app '+strAppName+' on instance '+strInstanceName;
		aTasks.push(task);
	}

	status.tasks = aTasks;

	return status;
}
